import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
        {name: 'Max', age: 28},
        {name: 'Manu', age: 29},
        {name: 'F', age: 26}
    ]
  }

  switchNameHandler = (newName) => {
    // console.log('Was clicked');
      // because we use =>, this here will refer to the class App
      // Don't do this.state.persons[0].name = "FMax";
      this.setState({
          persons: [
              {name: newName, age: 28},
              {name: 'Manu', age: 29},
              {name: 'F', age: 27}
          ]
      })
  }

  nameChangedHandler = (event) => {
      this.setState({
          persons: [
              {name: 'Max', age: 28},
              {name: event.target.value, age: 29},
              {name: 'F', age: 26}
          ]
      })
  }

  render() {
    return (
      <div className="App">
          <h1>Hi, I'm a React App!</h1>
          <button onClick={() => this.switchNameHandler('OO')}>Switch Name</button>
          <Person
              name={this.state.persons[0].name}
              age={this.state.persons[0].age}/>
          <Person
              name={this.state.persons[1].name}
              age={this.state.persons[1].age}
              click={this.switchNameHandler.bind(this,'LL')}
              changed={this}>My Hobby: F</Person>
          <Person
              name={this.state.persons[2].name}
              age={this.state.persons[2].age}/>
      </div>
    );
  }
}

export default App;
