import React from 'react';

const withClass = (WrappedComponent, className) => {
    // ...props: pass on the props as you get them
    return (props) => (
        <div className={className}>
            <WrappedComponent ref={props.forwardedRef}{...props} />
        </div>
    )
}

export default withClass;
