import React, { Component } from 'react';
import classes from './App.css';
import Person from './Person/Person';
import ErrorBoundary from './ErrorBoundary/ErrorBoundary';

class App extends Component {
  state = {
    persons: [
        {id:'baba', name: 'Max', age: 28},
        {id:'adad', name: 'Manu', age: 29},
        {id:'adsf', name: 'F', age: 26}
    ]
  }

  deletePersonHandler = (personIndex) => {
      // create a copy of your array
      // const persons = this.state.persons.slice();
      const persons = [...this.state.persons];
      persons.splice(personIndex,1);
      this.setState({persons:persons});
  }

  nameChangedHandler = (event, id) => {
      // execute for every person in the persons array
      const personIndex = this.state.persons.findIndex(p => {
          return p.id === id;
      });

      const person = {
          ...this.state.persons[personIndex]
      };

      person.name = event.target.value;

      const persons = [...this.state.persons];
      persons[personIndex] = person;

      this.setState({
          persons:persons
      });
  }

  togglePersonsHandler = () => {
    // this keyword always refer to the class
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow});
  }

  render() {
    let persons = null;
    let btnClass = '';

    if (this.state.showPersons) {
      persons = (
          <div>
              {this.state.persons.map((person, index) => {
                  // return JSX here
                  return
                  <ErrorBoundary>
                      <Person name={person.name}
                                     age={person.age}
                                     key={person.id}
                                     changed={(event) => this.nameChangedHandler(event, person.id)}
                                     click={() => this.deletePersonHandler(index)}/>
                  </ErrorBoundary>
              })}
          </div>
      );

      btnClass = classes.Red;
    }

    // turn array of strings into one string => "red bold"
    let assignedClasses = [];
    if (this.state.persons.length <= 2) {
        assignedClasses.push(classes.red); // classes = ['red']
    }
    if (this.state.persons.length <= 1) {
        assignedClasses.push(classes.bold);
    }

    return (
          <div className={classes.App}>
              <h1>Hi, I'm a React App!</h1>
              <p className={assignedClasses.join(' ')}>This is working!</p>
              <button
                  className={btnClass}
                  onClick={this.togglePersonsHandler}>Switch Name</button>
              {persons}
          </div>
    );
  }
}

export default App;
